<?phpadd_action('wp_ajax_my_ajax_action', 'pwr_ads_ajax_action');function pwr_ads_ajax_action(){    if (isset($_POST['action']) && isset($_POST['option1'])) {        update_option('pwr_ads_option_1', sanitize_text_field($_POST['option1']));        echo 'Key successfully updated';    } else {        echo 'updating';    }    wp_die();}// Ajax test for fronted // Action will work for only logged in usersadd_action('wp_ajax_my_ajax_fronted_action', 'pwr_ads_my_ajax_fronted_action');// Action will work for logged in and logged out both users// add_action('wp_ajax_nopriv_my_ajax_fronted_action', 'pwr_ads_my_ajax_fronted_action'); function pwr_ads_my_ajax_fronted_action(){    if (isset($_POST['action']) && isset($_POST['value'])) {        echo absint($_POST['value']) + 20;    } else {        echo 'Error';    }    wp_die();}