<?phpdefined('ABSPATH') || die('You are not allowed to acces this page');// PLUGIN ACTICATION HOOKregister_activation_hook(PWRADS_PLUGIN_FILE, 'pwr_ads_db_activation');function pwr_ads_db_activation(){    global $wpdb;    $tablename = $wpdb->prefix . "pwrads_"; // DEFINED TABLE PREFIX    $charset_collate = $wpdb->get_charset_collate();    require_once(ABSPATH . 'wp-admin/includes/upgrade.php');    $sql_1 = "CREATE TABLE IF NOT EXISTS " . $tablename . "users (    id INT (10) NOT NULL AUTO_INCREMENT,          img VARCHAR (1000),        name varchar(80) NOT NULL,        username varchar(80) NOT NULL,        email varchar(80) NOT NULL,        PRIMARY KEY  (id)      ) $charset_collate;";    dbDelta($sql_1);    $sql_2 = "CREATE TABLE IF NOT EXISTS " . $tablename . "ads (    id INT (10) NOT NULL AUTO_INCREMENT,    unique_user_id varchar(225) NOT NULL,    PRIMARY KEY  (id)  ) $charset_collate;";    dbDelta($sql_2);    $sql_3 = "CREATE TABLE IF NOT EXISTS " . $tablename . "token (    id INT (10) NOT NULL AUTO_INCREMENT,        token varchar(80) NOT NULL,        push_ntc_token varchar(100) NOT NULL,        autoads  varchar(255) NOT NULL,        PRIMARY KEY  (id)      ) $charset_collate;";    dbDelta($sql_3);    //add_option( "pwr_ads_license_key"); // CREATING OPTION FORM OPTIONS TABLE}// PLUGIN DEACTICATION HOOKregister_deactivation_hook(PWRADS_PLUGIN_FILE, 'pwr_ads_db_deactivation');function pwr_ads_db_deactivation(){    global $wpdb;    $results = $wpdb->get_results("SELECT * FROM wp_pwrads_ads");    $unique_user_id = "";    foreach ($results as $row) {        $unique_user_id = $row->unique_user_id;        $url = 'https://portal-pwr-ads.com/is_disconnected/' . $unique_user_id;        $request = wp_remote_retrieve_body(wp_remote_get($url));    }    $tbl_array = [        $wpdb->prefix . "pwrads_users",        $wpdb->prefix . "pwrads_ads",        $wpdb->prefix . "pwrads_token",    ];    foreach ($tbl_array as $tbl_name) {        $wpdb->query("DROP TABLE IF EXISTS $tbl_name");    }    //delete_option("pwr_ads_license_key"); // REMOVING OPTION FORM OPTIONS TABLE}