<?php$template_name = 'full_screen_popup';$url = ' https://portal-pwr-ads.com/getautoad/full_screen_popup ';$request = wp_remote_retrieve_body(wp_remote_get($url));//print_r(json_decode( $request));       if (is_wp_error($request)) {    return false; // bail early    }$data = json_decode($request);if ($data != null) {    if (isset($data->source)) {        $encryption = $data->script;        // Store the cipher method        $ciphering = "AES-128-CTR";        // Use OpenSSl Encryption method        $iv_length = openssl_cipher_iv_length($ciphering);        $options = 0;        // Non-NULL Initialization Vector for decryption        $decryption_iv = '1234567891011121';        // Store the decryption key        $decryption_key = "PwrAdsScript";        // Use openssl_decrypt() function to decrypt the data        $decryption = openssl_decrypt($encryption, $ciphering, $decryption_key, $options, $decryption_iv);        if ($data !== null && $data->source == "google_adsense") {?>            <script type="text/javascript">                var ads_full_screen_popup = '<?php echo esc_js($decryption); ?>';                ads_full_screen_popup = ads_full_screen_popup.replaceAll('&gt;', '>').replaceAll('&quot;', '"').replaceAll('&lt;', '<').replaceAll(/\s\s+/g, ' ').replaceAll("</' + '", "</");                ads_full_screen_popup = ads_full_screen_popup.replace('ins class="adsbygoogle"', 'ins class="adsbygoogle ads_full_screen_popup"');                document.write(ads_full_screen_popup);            </script>        <?php        } elseif ($data !== null && $data->source == "adkernel") {            //echo esc_attr( $decryption );        ?>            <script type="text/javascript">                var ads_full_screen_popup = '<?php echo esc_js($decryption); ?>';                ads_full_screen_popup = ads_full_screen_popup.replaceAll('&gt;', '>').replaceAll('&quot;', '"').replaceAll('&lt;', '<').replaceAll(/\s\s+/g, ' ').replaceAll("</' + '", "</");                ads_full_screen_popup = ads_full_screen_popup.replace('ins class="adsbygoogle"', 'ins class="adsbygoogle ads_full_screen_popup"');                document.write(ads_full_screen_popup);            </script>            <div id="full_screen_popup"></div>            <script type="text/javascript">                function PopUp(hideOrshow) {                    if (hideOrshow == 'hide') document.getElementById('pwrads_pop-wrap').style.display = "none";                    else document.getElementById('pwrads_pop-wrap').removeAttribute('style');                }                window.onload = function() {                    setTimeout(function() {                        PopUp('show');                    }, 5000);                }                function removefull_screen_popupImage(e) {                    document.getElementById("pwrads_pop-wrap").remove();                }            </script>        <?php }    } else {        ?>        <div id="pwrads_pop-wrap" style='display:none' onclick='removeFull_Image()'>            <div id="pwrads_popupp">                <div class="popup-header">                    <div class="ads-text-col">                        <span></span>                        <a href="https://pwr-ads.com/ads-by-pwr-ads/" target="_blank" class="img_ad"><img src="<?php echo plugin_dir_url(dirname(__FILE__)) . 'asst/img/banner-logo.png'; ?>"></a><input type="submit" class="pwrads_close" name="Close" value="X" onclick="PopUp('hide')">                    </div>                </div><?php if ($data == Null) { ?> <div class="content-section">                        <center><img id="popImgscr" src="<?php echo plugin_dir_url(dirname(__FILE__)) . 'asst/img/default_img_for_ads.png'; ?>"> </center>                    </div> <?php } else { ?> <a href="https://portal-pwr-ads.com/getautoadurl/<?php echo esc_attr($data->unique_token . '/pwrsrc=' . esc_attr($_SERVER['SERVER_NAME']) . '/id=' . $data->id . '/adtype=autoad'); ?>" target="_blank">                        <div class="content-section">                            <center><img id="popImgscr" src="<?php echo esc_url($data->imgurl);                                                            } ?>" /> </center>                        </div>                    </a>            </div>        </div>        <script type="text/javascript">            function PopUp(hideOrshow) {                if (hideOrshow == 'hide') document.getElementById('pwrads_pop-wrap').style.display = "none";                else document.getElementById('pwrads_pop-wrap').removeAttribute('style');            }            window.onload = function() {                setTimeout(function() {                    PopUp('show');                }, 5000);            }            function removeFull_Image(e) {                document.getElementById("pwrads_pop-wrap").remove();            }        </script><?php }}?>