<?php$template_name = 'footer-temp';$url = 'https://portal-pwr-ads.com/getad/footer';$request = wp_remote_retrieve_body(wp_remote_get($url));//print_r(json_decode( $request));if (is_wp_error($request)) {    return false; // Bail early}$data = json_decode($request);if ($data == null && is_site_admin()) {?>    <!--   *****SCRIPT TO SHOW ADS BEFORE FOOTER START*****   -->    <script type="text/javascript">        var footer = document.getElementsByTagName("footer")[0];        //var footerdiv = document.createElement('div');        var string = '<div id="footer_image" class="pwr_ads_responsive"><span onclick="removeFooterImage()" class="ad_banner_logo"><div class="ads-text"></div><a href="<?php echo "https://pwr-ads.com/ads-by-pwr-ads/"; ?>" target="_blank" class="img_ad2"><img id="theImg" src="<?php echo plugin_dir_url(dirname(__FILE__)) . 'asst/img/default_img_for_ads.png'; ?>" ></a><a href="https://pwr-ads.com/ads-by-pwr-ads/" target="_blank" class="img_ad"><img src="<?php echo plugin_dir_url(dirname(__FILE__)) . 'asst/img/banner-logo.png'; ?>"></a><div class="ads-text close-icon"><span>X</span></div></span></div>'        footer.insertAdjacentHTML('beforebegin', string);        function removeFooterImage(e) {            document.getElementById("footer_image").remove();        }    </script><?php} elseif ($data !== null) { ?>    <script type="text/javascript">        var footer = document.getElementsByTagName("footer")[0];        //var footerdiv = document.createElement('div');        var string = '<div id="footer_image" class="pwr_ads_responsive"><span onclick="removeFooterImage()" class="ad_banner_logo"><div class="ads-text"></div><a href="https://portal-pwr-ads.com/getadurl/<?php echo esc_attr($data->unique_token . '/pwrsrc=' . esc_attr($_SERVER['SERVER_NAME']) . '/id=' . $data->id . '/adtype=customad'); ?>" target="_blank" class="img_ad2"><img id = "theImg" src="<?php echo esc_url($data->imgurl); ?>" ></a><a href="https://pwr-ads.com/ads-by-pwr-ads/" target="_blank" class="img_ad"><img src="<?php echo plugin_dir_url(dirname(__FILE__)) . 'asst/img/banner-logo.png'; ?>"></a><div class="ads-text close-icon"><span>X</span></div></span></div>'        footer.insertAdjacentHTML('beforebegin', string);        function removeFooterImage(e) {            document.getElementById("footer_image").remove();        }    </script><?php}?>