<?php$template_name = 'home-pop-up';$url_script = 'https://portal-pwr-ads.com/getadsensescript/pop_up';$request_script = wp_remote_retrieve_body(wp_remote_get($url_script));//print_r(json_decode($request_script));if ($request_script == "") {    return false; // Bail early} else {    $data_script = json_decode($request_script);    $encryption = $data_script->script;    // Store the cipher method    $ciphering = "AES-128-CTR";    // Use OpenSSl Encryption method    $iv_length = openssl_cipher_iv_length($ciphering);    $options = 0;    // Non-NULL Initialization Vector for decryption    $decryption_iv = '1234567891011121';    // Store the decryption key    $decryption_key = "PwrAdsScript";    // Use openssl_decrypt() function to decrypt the data    $decryption = openssl_decrypt(        $encryption,        $ciphering,        $decryption_key,        $options,        $decryption_iv    );}if ($data_script == null && is_site_admin()) {?>    <div id="pwrads_pop-wrapper" style='display:none'>        <div id="pwrads_popup">            <div class="popup-header">                <div class="ads-text-col">                    <span></span>                    <a href="https://pwr-ads.com/ads-by-pwr-ads/" class="img_ad"><img src="<?php echo esc_attr(plugin_dir_url(dirname(__FILE__))) . 'asst/img/banner-logo.png'; ?>"></a>                    <input type="submit" class="pwrads_close" name="Close" value="X" onclick="PopUp('hide')">                </div>            </div>            <div class="content-section">                <a href="<?php if ($data_script == null) {                                echo esc_attr("https://portal-pwr-ads.com/");                            } else {                                echo esc_attr("https://portal-pwr-ads.com/getadurl/");                                echo esc_attr($data_script->unique_token);                            } ?>">                    <center>                        <img id="popImg" src="<?php echo esc_attr(plugin_dir_url(dirname(__FILE__))) . 'asst/img/default_img_for_ads.png'; ?> " />                    </center>                </a>            </div>        </div>    </div>    <script type="text/javascript">        function PopUp(hideOrshow) {            if (hideOrshow == 'hide') document.getElementById('pwrads_pop-wrapper').style.display = "none";            else document.getElementById('pwrads_pop-wrapper').removeAttribute('style');        }        window.onload = function() {            setTimeout(function() {                PopUp('show');            }, 1000);        }    </script><?php} elseif ($data_script !== null && $data_script->source == "google_adsense") {?>    <script type="text/javascript">        var ads_pop_up = '<?php echo esc_js($decryption); ?>';        ads_pop_up = ads_pop_up.replaceAll('&gt;', '>').replaceAll('&quot;', '"').replaceAll('&lt;', '<').replaceAll(/\s\s+/g, ' ').replaceAll("</' + '", "</");        document.write(ads_pop_up);    </script><?php} elseif ($data_script !== null && $data_script->source == "adkernel") {?>    <script type="text/javascript">        var ads_pop_up = '<?php echo esc_js($decryption); ?>';        ads_pop_up = ads_pop_up.replaceAll('&gt;', '>').replaceAll('&quot;', '"').replaceAll('&lt;', '<').replaceAll(/\s\s+/g, ' ').replaceAll("</' + '", "</");        document.write(ads_pop_up);    </script>    <div id="pop_up"></div>    <script type="text/javascript">        function PopUp(hideOrshow) {            if (hideOrshow == 'hide') document.getElementById('pwrads_pop-wrapper').style.display = "none";            else document.getElementById('pwrads_pop-wrapper').removeAttribute('style');        }        window.onload = function() {            setTimeout(function() {                PopUp('show');            }, 1000);        }        function removepop_upImage(e) {            document.getElementById("pwrads_pop-wrapper").remove();        }    </script><?php }?>