<?php$template_name = 'full-screen-popup';$url_script = 'https://portal-pwr-ads.com/getadsensescript/full_screen_popup';$request_script = wp_remote_retrieve_body(wp_remote_get($url_script));//print_r(json_decode($request_script));if ($request_script == "") {    return false; // Bail early} else {    $data_script = json_decode($request_script);    $encryption = $data_script->script;    // Store the cipher method    $ciphering = "AES-128-CTR";    // Use OpenSSl Encryption method    $iv_length = openssl_cipher_iv_length($ciphering);    $options = 0;    // Non-NULL Initialization Vector for decryption    $decryption_iv = '1234567891011121';    // Store the decryption key    $decryption_key = "PwrAdsScript";    // Use openssl_decrypt() function to decrypt the data    $decryption = openssl_decrypt(        $encryption,        $ciphering,        $decryption_key,        $options,        $decryption_iv    );}if ($data_script == null && is_site_admin()) {} elseif ($data_script !== null && $data_script->source == "google_adsense") {?>    <script type="text/javascript">        var ads_full_screen_popup = '<?php echo esc_js($decryption); ?>';        ads_full_screen_popup = ads_full_screen_popup.replaceAll('&gt;', '>').replaceAll('&quot;', '"').replaceAll('&lt;', '<').replaceAll(/\s\s+/g, ' ').replaceAll("</' + '", "</");        document.write(ads_full_screen_popup);    </script><?php} elseif ($data_script !== null && $data_script->source == "adkernel") {?>    <script type="text/javascript">        var ads_full_screen_popup = '<?php echo esc_js($decryption); ?>';        ads_full_screen_popup = ads_full_screen_popup.replaceAll('&gt;', '>').replaceAll('&quot;', '"').replaceAll('&lt;', '<').replaceAll(/\s\s+/g, ' ').replaceAll("</' + '", "</");        document.write(ads_full_screen_popup);    </script>    <div id="full_screen_popup"></div>    <script type="text/javascript">        function PopUp(hideOrshow) {            if (hideOrshow == 'hide') document.getElementById('pwrads_pop-wrap').style.display = "none";            else document.getElementById('pwrads_pop-wrap').removeAttribute('style');        }        window.onload = function() {            setTimeout(function() {                PopUp('show');            }, 5000);        }        function removefull_screen_popupImage(e) {            document.getElementById("pwrads_pop-wrap").remove();        }    </script><?php }?>